import React from "react";
import { Item } from "./Item";
import { useState } from "react";

export const Items = ({ items, onAdd, onShowItem, arr }) => {

    const [filter, setFilter] = useState('')

    const filterProducts = arr.filter(product => {
        return product.title.toLowerCase().includes(filter.toLowerCase())
    })

    return (

        <div className="items">

            {filterProducts.map(el => (
                <Item key={el.id} item={el} onAdd={onAdd} onShowItem={onShowItem} />
            ))}
        </div>
    )
}
