import React, { useState } from 'react'

export default function Header() {

    let [cartOpen, setCartOpen] = useState(false);

    return (
        <header>
            <div className='header'>
                <span className='logo'>House Stuff</span>
                <ul className='nav'>
                    <button onClick={() => setCartOpen(cartOpen = !cartOpen)} className={`btn_bag ${cartOpen && 'btn_bag-active'}`}><img className='nav__img nav__item' src="./img/cart.png" alt='Корзина' /></button>
                    {cartOpen && (
                        <div className='shop-cart'>

                        </div>
                    )}
                    <li className='nav__item'>О нас</li>
                    <li className='nav__item'>Контакты</li>
                    <li className='nav__item'>Личный кабинет</li>
                </ul>
            </div>
            <div className='presentation'></div>
        </header >
    )
}
