import React from "react";
import { Item } from "./Item";
import { useState } from "react";

export const Items = ({ items, onAdd, onShowItem, arr }) => {

    const [filter, setFilter] = useState('')

    const filterProducts = arr.filter(product => {
        return product.title.toLowerCase().includes(filter.toLowerCase())
        console.log(filterProducts);
    })

    return (

        <div className="items">
            <input placeholder='Поиск...' className='search' type='text'
                onChange={e => setFilter(e.target.value)}
            />
            {filterProducts.map(el => (
                <Item key={el.id} item={el} onAdd={onAdd} onShowItem={onShowItem} />
            ))}
        </div>
    )
}
