import React, { useState } from 'react'
import Orders from './Orders';

const showOrders = (orders, onDelete) => {
    let sum = orders.reduce((sum, el) => {
        return sum + +el.price;
    }, 0)

    return (<>
        {orders.map(el => (
            <Orders key={el.id} item={el} onDelete={onDelete} />
        ))}
        <div className='sum'>Сумма заказа:<span className='sum-value'>{new Intl.NumberFormat().format(sum)}$</span></div>
    </>)
}

const showNothing = () => {
    return (
        <div className='empty '>
            <h2>Товаров нет</h2>
        </div>
    )
}

export default function Header({ orders, onDelete }) {

    let [cartOpen, setCartOpen] = useState(false);

    return (
        <header>
            <div className='header'>
                <span className='logo'>House Stuff</span>
                <ul className='nav'>
                    <button onClick={() => setCartOpen(cartOpen = !cartOpen)} className={`btn_bag ${cartOpen && 'btn_bag-active'}`}>
                        <img className='nav__img nav__item' src="./img/cart.png" alt='Корзина' />
                    </button>
                    {cartOpen && (
                        <div className='shop-cart'>
                            {orders.length > 0 ?
                                showOrders(orders, onDelete) : showNothing()}
                        </div>
                    )}
                    <li className='nav__item'>О нас</li>
                    <li className='nav__item'>Контакты</li>
                    <li className='nav__item'>Личный кабинет</li>
                </ul>
            </div>
            <div className='presentation'></div>
        </header >
    )
}
