import Header from "./components/Header";
import Footer from "./components/Footer";
import { Items } from "./components/Items";
import { useState, useEffect, useMemo } from "react";
import { Categories } from "./components/Categories";
import { ShowFullItem } from "./components/ShowFullItem";
import axios from "axios";




function App() {

  const [arr, setArr] = useState([
    {
      id: 1,
      title: 'Стул серый',
      image: 'chair-grey.jpg',
      category: 'chairs',
      description: 'Numquam repellendus a magnam',
      price: '49.99'
    },

    {
      id: 2,
      title: 'Стол в стиле лофт',
      image: 'table-loft.jpg',
      category: 'tables',
      description: 'Ipsam aperiam voluptates qui',
      price: '109.00'
    },

    {
      id: 3,
      title: 'Диван',
      image: 'sofa-loft.jpg',
      category: 'sofas',
      description: 'Repellendus sunt dolores architecto voluptatum',
      price: '219.99'
    },

    {
      id: 4,
      title: 'Светильник лофт',
      image: 'light-loft.jpg',
      category: 'lights',
      description: 'Excepturi deleniti adipisci voluptatem et neque optio illum',
      price: '59.99'
    },

    {
      id: 5,
      title: 'Диван лофт',
      image: 'divan-loft3.jpg',
      category: 'sofas',
      description: 'Pariatur et magnam ea doloribus similique voluptatem rerum quia',
      price: '250.00'
    },

    {
      id: 6,
      title: 'Стул красный',
      image: 'chair-red.jpg',
      category: 'chairs',
      description: 'Reiciendis est magnam amet nemo iste recusandae impedit',
      price: '55.00'
    },
  ])

  // async function fetchStore() {
  //   const response = await axios.get('https://fakestoreapi.com/products')
  //   // console.log(response.data);
  //   setArr(response.data)
  // }

  // useEffect(() => {
  //   fetchStore()
  // }, [])

  const [orders, setOrders] = useState([])
  const [currentItems, setCurrentItems] = useState(arr)
  const [showFullItem, setShowFullItem] = useState(false)
  const [fullItem, setFullItem] = useState({})
  const [cartOpen, setCartOpen] = useState(false)
  const [filter, setFilter] = useState('')

  useMemo(() => {
    const filterProducts = arr.filter(product => {
      return product.title.toLowerCase().includes(filter.toLowerCase())
    })
  }, [])
  // const filterProducts = arr.filter(product => {
  //   return product.title.toLowerCase().includes(filter.toLowerCase())
  // })

  const sortedProduct = useMemo(() => {
    return filterProducts
  }, [filterProducts, arr, filter])

  // function addToOrder(item) {
  //   let isInArr = false
  //   orders.forEach(el => {
  //     if (el.id === item.id)
  //       isInArr = true
  //   })
  //   if (!isInArr)
  //     setOrders([...orders, item])
  // }

  function addToOrder(item) {

    const id = orders.length === 0 ? 1 : orders[orders.length - 1].id + 1;

    const newItem = {
      id: id,
      title: item.title,
      image: item.image,
      category: item.category,
      description: item.description,
      price: item.price
    }
    setOrders([...orders, newItem])
  }

  const deleteOrder = (id) => {
    setOrders(orders.filter((el) => el.id !== id))
  }

  const chooseCategory = (category) => {
    if (category === 'all') {
      setCurrentItems(arr)
      return
    }
    setCurrentItems(arr.filter((el) => el.category === category))
  }

  const onShowItem = (item) => {
    setFullItem(item)
    setShowFullItem(!showFullItem)
  }

  return (
    <div className="wrapper">

      <Header orders={orders} onDelete={deleteOrder} cartOpen={cartOpen} setCartOpen={setCartOpen} setFilter={setFilter} />
      <Categories chooseCategory={chooseCategory} />
      <Items onShowItem={onShowItem} items={sortedProduct} onAdd={addToOrder} arr={arr} />
      {showFullItem && <ShowFullItem item={fullItem} onAdd={addToOrder}
        onShowItem={onShowItem} setShowFullItem={setShowFullItem} />}
      <Footer />
    </div>
  );
}

export default App;
