import Header from "./components/Header";
import Footer from "./components/Footer";
import { Items } from "./components/Items";
import { useState } from "react";


function App() {

  const [arr, setArr] = useState([
    {
      id: 1,
      title: 'Стул серый',
      image: 'chair-grey.jpg',
      category: 'chairs',
      description: 'Numquam repellendus a magnam',
      price: '49.99'
    },

    {
      id: 2,
      title: 'Стол в стиле лофт',
      image: 'table-loft.jpg',
      category: 'tables',
      description: 'Ipsam aperiam voluptates qui',
      price: '109.00'
    },

    {
      id: 3,
      title: 'Диван',
      image: 'sofa-loft.jpg',
      category: 'sofas',
      description: 'Repellendus sunt dolores architecto voluptatum',
      price: '219.99'
    },

    {
      id: 4,
      title: 'Светильник лофт',
      image: 'light-loft.jpg',
      category: 'lights',
      description: 'Excepturi deleniti adipisci voluptatem et neque optio illum ad',
      price: '59.99'
    },

    {
      id: 5,
      title: 'Диван лофт',
      image: 'divan-loft3.jpg',
      category: 'sofas',
      description: 'Pariatur et magnam ea doloribus similique voluptatem rerum quia',
      price: '250.00'
    },

    {
      id: 6,
      title: 'Стул красный',
      image: 'chair-red.jpg',
      category: 'chairs',
      description: 'Reiciendis est magnam amet nemo iste recusandae impedit quaerat',
      price: '55.00'
    },
  ])

  const [orders, setOrders] = useState([])

  function addToOrder(item) {
    setOrders([...orders, item])
    console.log(orders);
  }

  return (
    <div className="wrapper">
      <Header orders={orders} />
      <Items items={arr} onAdd={addToOrder} />
      <Footer />
    </div>
  );
}

export default App;
