import React from "react";
import { Item } from "./Item";

export const Items = ({ items, onAdd, onShowItem, filterProducts }) => {
    return (

        <div className="items">
            <input placeholder='Поиск...' className='search' type='text'
                onChange={e => setFilter(e.target.value)}
            />
            {items.map(el => (
                <Item key={el.id} item={el} onAdd={onAdd} onShowItem={onShowItem} />
            ))}
        </div>
    )
}
